          <Icon
            name="mouse"
            size={15}
            color="white"
          />
        }
          buttonStyle={{ borderRadius: 0, marginLeft: 90, marginRight: 90, marginBottom: 0, borderRadius:20}}
          title=' View detail' />
      </Card>
    </View>
  );

  const renderItem = ({ item}) => (
    <Item item={item} />
  ); 





  const data1 = {
    labels: ['Ene', 'Feb', 'Mar', 'Abr', 'May', 'Jun'],
    datasets: [
      {
        data: [20, 45, 28, 80, 99, 43]
      }
    ]
  }

  const screenWidth = Dimensions.get('window').width

  const chartConfig = {
    backgroundGradientFrom: '#1E2923',
    backgroundGradientTo: '#08130D',
    color: (opacity = 1) => `rgba(26, 255, 146, ${opacity})`
  }

  const LineChartExample = () => {
    return (
      <LineChart
        data={data1}
        width={screenWidth}
        height={220}
        chartConfig={chartConfig}
        svg={{ stroke: 'rgba(26, 255, 146, 0.6)' }}
        style={{ borderRadius: 16 }}
      />
    )
  }


  return(

  <View style={{marginTop:60,paddingTop:20, backgroundColor:"#E1FCFF"}}>

    <View style = {{
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#F5FCFF'
      }}
      >
      <LineChartExample/>

    </View>

   <TextInput
          placeholder='keyword..'
           label='Name'
            style={{width: 330,
            height: 40,
            padding: 10,
            borderWidth: 1,
            borderColor: 'black',
            marginBottom: 30,
            marginStart:30,
            backgroundColor:"white",
            borderRadius: 20}}
        />

        <Button
              buttonStyle={{marginStart:80,marginEnd:80,height:50, borderRadius:20, backgroundColor:"#5bc0de",
              shadowColor: '#171717',
